mapper.select:
select form1.form_id, form1.name, form1.code, form1.status, form1.form_config, form1.widget_list, form1.version, form1.remark, form1.del_flag, form1.create_by, form1.create_time, form1.update_by, form1.update_time from cmd_form form1 INNER JOIN ( SELECT form2.form_id,form2.code, MAX( form2.version ) AS version FROM cmd_form form2 GROUP BY form2.code ) form3 ON form1.version = form3.version
AND form1.code = form3.code and form1.name like concat('%', #{name}, '%') and form1.code like concat('%', #{code}, '%') and form1.status = #{status}
order by form1.create_time desc
