mapper.select:
select m1.model_id, m1.name, m1.code, m1.version, m1.bpmn_xml, m1.deployment_id,m1.status,m1.node_json_data, m1.description, m1.del_flag, m1.create_by, m1.create_time, m1.update_by, m1.update_time, m1.deploy_time from cmd_model m1 INNER JOIN ( SELECT m2.model_id,m2.code, MAX( m2.version ) AS version FROM cmd_model m2 GROUP BY m2.code ) m3 ON m1.version = m3.version
AND m1.code = m3.code AND m1.del_flag = 0 and name like concat('%', #{name}, '%') and key = #{code}
order by m1.create_time desc
